import test from 'ava'
import { schemaToType } from '../../index.js'

/**
 * These tests only verify that the binary and the `index.js` file generated by Napi are working correctly in Node
 */

test('basic object schema', (t) => {
  const schema = {
    type: 'object',
    properties: {
      id: { type: 'string' },
      title: { type: 'string' },
      author: { type: 'string' },
      publishedDate: { type: 'string', format: 'date' },
      rating: { type: 'number', format: 'float' },
      age: { type: 'integer' },
    },
  }

  const result = schemaToType(schema, { name: 'Book' })

  t.snapshot(result)
})


test('prefer interface over type for simple object', (t) => {
  const schema = {
    type: 'object',
    properties: {
      name: { type: 'string' },
      age: { type: 'number' }
    }
  }

  const result = schemaToType(schema, {
    name: 'Person',
    preferUnknownOverAny: false,
    preferInterfaceOverType: true
  })

  t.snapshot(result)
})

test.skip('schema with unknown types', (t) => {
  const schema = {
    type: 'object',
    properties: {
      id: {
        type: 'string'
      },
      dynamicValue: {},
      arrayOfAny: {
        type: 'array',
        items: {}
      },
      objectWithAny: {
        type: 'object',
        properties: {
          anyProp: {}
        }
      }
    },
    required: ['id']
  }


  const result = schemaToType(schema, {
    name: 'SchemaWithUnknown',
    preferUnknownOverAny: true,
    preferInterfaceOverType: false
  })

  t.snapshot(result)
})
