import test from 'ava'
import { schemaToTypescript } from '../../index.js'

/**
 * These tests only verify that the binary and the `index.js` file generated by Napi are working correctly in Node
 */

test('basic object schema to typescript', (t) => {
  const schema = {
    type: 'object',
    properties: {
      id: { type: 'string' },
      title: { type: 'string' },
      author: { type: 'string' },
      publishedDate: { type: 'string', format: 'date' },
      rating: { type: 'number', format: 'float' },
      age: { type: 'integer' },
    },
  }

  const result = schemaToTypescript('Book', schema)
  t.snapshot(result)
})


test('prefer interface over type for simple object', (t) => {
  const schema = {
    type: 'object',
    properties: {
      name: { type: 'string' },
      age: { type: 'number' }
    }
  }

  const config = {
    preferUnknownOverAny: false,
    preferInterfaceOverType: true
  }

  const result = schemaToTypescript('Person', schema, config)
  t.snapshot(result)
})

test('schema with unknown types', (t) => {
  const schema = {
    type: 'object',
    properties: {
      id: {
        type: 'string'
      },
      dynamicValue: {},
      arrayOfAny: {
        type: 'array',
        items: {}
      },
      objectWithAny: {
        type: 'object',
        properties: {
          anyProp: {}
        }
      }
    },
    required: ['id']
  }

  const config = {
    preferUnknownOverAny: true,
    preferInterfaceOverType: false
  }

  const result = schemaToTypescript('SchemaWithUnknown', schema, config)
  t.snapshot(result)
})